r = 3
delivery = [[1, 5],[8, 3],[4, 2],[2, 3],[3, 1],[3, 2],[4, 2],[5, 2],[4, 1]]
near = [(0, 1),(1, 0), (0, -1), (-1, 0)]


time_max = 0
for a, b in delivery:
    if time_max < a:
        time_max = a

mytip = delivery[0 * r + 0][1]
delivery[0 * r + 0][1] = 0

queue = [[0, 0, mytip]]
time = 0

while queue:
    time += 1
    print('time')
    print(time)
    print('queue')
    print(queue)

    if time > time_max:
        break

    for i in range(len(queue)):
        x, y, tips = queue.pop(0)

        for a, b in near:
            xi, yi = x + a, y + b

            if 0 <= xi < r and 0 <= yi < r and delivery[xi * r + yi][0] >= time:
                queue.append([xi, yi, tips + delivery[xi * r + yi][1]])
                delivery[xi * r + yi][1] = 0
            elif 0 <= xi < r and 0 <= yi < r:
                if delivery[xi * r + yi][1] == 0:
                    queue.append([xi, yi, tips + delivery[xi * r + yi][1]])


